import React, { useRef } from "react";
import NoteContext from '../Context/Notes/NoteContext'
import { useContext } from 'react'
import NotesItem from "./NotesItem";
import AddNotes from "./AddNotes";
import { useEffect, useState } from "react";

const Notes = () => {
    const { notes, getNotes, editNote } = useContext(NoteContext);
    const ref = useRef(null);

    useEffect(() => {
        getNotes();
    }, [])

    const [note, setNote] = useState({ eId: '', etitle: '', edescription: '', etag: '' })

    const handleSubmit = (e) => {
    }

    const handleChange = (e) => {
        console.log(e);
        setNote({ ...note, [e.target.name]: e.target.value })
    }

    const updateNote = (currentNote) => {
        console.log("hello")
        ref.current.click();
    }

    return (
        <>
            <AddNotes />
            <button type="button" ref={ref} class="btn btn-primary " data-toggle="modal" data-target="#exampleModal">
                Launch demo modal
            </button>

            <div class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div class="modal-dialog" role="document">
                    <div class="modal-content">
                        <div class="modal-header">
                            <h5 class="modal-title" id="exampleModalLabel">Edit Note</h5>
                            <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                                <span aria-hidden="true">&times;</span>
                            </button>
                        </div>
                        <div class="modal-body">
                            <form className='my-3'>
                                <div className="mb-3">
                                    <label htmlFor="exampleInputEmail1" className="form-label">Title</label>
                                    <input type="text" className="form-control" id="title" name="title" aria-describedby="emailHelp" onChange={handleChange} />
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="exampleInputPassword1" className="form-label">Description</label>
                                    <input type="text" className="form-control" id="description" name="description" onChange={handleChange} />
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="exampleInputPassword1" className="form-label">Tag</label>
                                    <input type="text" className="form-control" id="tag" name="tag" onChange={handleChange} />
                                </div>
                                <button type="submit" className="btn btn-primary" onClick={handleSubmit}>Add Note</button>
                            </form>
                        </div>
                        <div class="modal-footer">
                            <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
                            <button type="button" class="btn btn-primary">Update Note</button>
                        </div>
                    </div>
                </div>
            </div>
            <div className="row my-3">
                <h2>Your Notes</h2>
                {notes.map((note) => {
                    return <NotesItem key={note._id} updateNote={updateNote} note={note} />
                })}
            </div>
        </>
    );
};

export default Notes;
