import React from "react";
import NoteContext from '../Context/Notes/NoteContext'
import { useContext } from 'react'
import NotesItem from "./NotesItem";
import AddNotes from "./AddNotes";
import { useEffect } from "react";

const Notes = () => {
    const {notes, getNotes, editNote} = useContext(NoteContext);

    useEffect(() => {
        getNotes();
    }, [])

    
    const [enote, setEnote] = useState({ id: '', etitle: '', edescription: '', etag: '' })

    const handleSubmit = (e) => {
        console.log(note._id)
        // e.preventDefault();
        console.log(note.title)
        note.title = enote.etitle;
        note.description = enote.edescription;
        note.tag = enote.etag;
        console.log(note.title)
        editNote(note._id, enote.etitle, enote.edescription, enote.etag);
    }

    const handleChange = (e) => {
        console.log(e);
        setEnote({ ...enote, [e.target.name]: e.target.value })
    }

    const updateNote = ()=>{
        <div className="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                        <div className="modal-dialog">
                            <div className="modal-content">
                                <div className="modal-header">
                                    <h5 className="modal-title" id="exampleModalLabel">Edit Note</h5>
                                    <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                                </div>
                                <div className="modal-body">
                                    <form className='my-3'>
                                        <div className="mb-3">
                                            <label htmlFor="exampleInputEmail1" className="form-label">Title</label>
                                            <input type="text" className="form-control" id="etitle" name="etitle" aria-describedby="emailHelp" onChange={handleChange} />
                                        </div>
                                        <div className="mb-3">
                                            <label htmlFor="exampleInputPassword1" className="form-label">Description</label>
                                            <input type="text" className="form-control" id="edescription" name="edescription" onChange={handleChange} />
                                        </div>
                                        <div className="mb-3">
                                            <label htmlFor="exampleInputPassword1" className="form-label">Tag</label>
                                            <input type="text" className="form-control" id="etag" name="etag" onChange={handleChange} />
                                        </div>
                                    </form>
                                </div>
                                <div className="modal-footer">
                                    <button type="submit" className="btn btn-primary" onClick={handleSubmit}>Save Changes</button>
                                </div>
                            </div>
                        </div>
                    </div>
    }

    return (
        <>
            <AddNotes />
            <div className="row my-3">
                <h2>Your Notes</h2>
                {notes.map((note) => {
                    return <NotesItem key={note._id} updateNote={updateNote} note={note} />
                })}
            </div>
        </>
    );
};

export default Notes;
