import React from 'react'
import { useContext } from 'react';
import { useState } from 'react';
import NoteContext from '../Context/Notes/NoteContext';

const NotesItem = (props) => {
    const { deleteNote, editNote } = useContext(NoteContext)
    const { note, updateNote } = props;
    console.log(props)

    const [enote, setEnote] = useState({ id: '', etitle: '', edescription: '', etag: '' })

    const handleSubmit = (e) => {
        console.log(note._id)
        // e.preventDefault();
        console.log(note.title)
        note.title = enote.etitle;
        note.description = enote.edescription;
        note.tag = enote.etag;
        console.log(note.title)
        editNote(note._id, enote.etitle, enote.edescription, enote.etag);
    }

    const handleChange = (e) => {
        console.log(e);
        setEnote({ ...enote, [e.target.name]: e.target.value })
    }

    return (
        <div className='col-md-3'>
            <div className="card my-3">
                <div className="card-body">
                    <div className="d-flex align-items-center">
                        <h5 className="card-title">{note.title}</h5>
                        <i className="far fa-trash-alt mx-3" onClick={() => { console.log(note._id); return deleteNote(note._id) }}></i>

                        <button type="button" className="btn" data-bs-toggle="modal" data-bs-target="#exampleModal">
                            <i className="far fa-edit" onClick={() => { console.log(note._id); }}></i>
                        </button>
                    </div>
                    <p className="card-text">{note.description}</p>
                    
                </div>
            </div>

        </div>
    )
}

export default NotesItem
