import NoteContext from "./NoteContext";
import { useState } from "react";

const NoteState = (props) => {
  // const s1 = {
  //     "name": "Nakshatra",
  //     "branch": "cse"
  // }
  // const [state, setState] = useState(s1);
  // const update = ()=>{
  //     setTimeout(() => {
  //         setState({
  //             "name": "Tanu",
  //             "branch": "It"
  //         })
  //     }, 1000);
  // }

  const host = "http://localhost:5000"

  const notesVal = []

  const getNotes = async()=>{
    const response = await fetch( `${host}/api/notes/fetchallnotes` , {
      method: 'GET', // *GET, POST, PUT, DELETE, etc.
      headers: {
        'Content-Type': 'application/json',
        'auth-token': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjFiODZlYjA4YWRiZjg5MzdlZmE1MDliIn0sImlhdCI6MTYzOTU2MzY2NX0.OSYsjyWNp-WqLa1dSw1ZXsgnDjgTZ0Wm5nBZwyUM1e8'
      },
    }); 
    // console.log(await response.json()); 
    setNotes(await response.json()); 
  }
  
  const [notes, setNotes] = useState(notesVal);
  
  const addNote = (title, description, tag) => {
    const note = {
      "_id": "61b9c326fc5c191451cad2296",
      "user": "61b86eb08adbf8937efa509b",
      "title": title,
      "description": description,
      "tag": tag,
      "date": "2021-12-15T10:27:50.438Z",
      "__v": 0
    }
    
    setNotes(notes.concat(note))
  }
  
  const deleteNote = async (id)=>{
    
    const response = await fetch( `${host}/api/notes/deletenote/${id}` , {
      method: 'DELETE', // *GET, POST, PUT, DELETE, etc.
      headers: {
        'Content-Type': 'application/json',
        'auth-token': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjFiODZlYjA4YWRiZjg5MzdlZmE1MDliIn0sImlhdCI6MTYzOTU2MzY2NX0.OSYsjyWNp-WqLa1dSw1ZXsgnDjgTZ0Wm5nBZwyUM1e8'
      },
    }); 
    
    console.log(response.json());
    
    // const newNotes = notes.filter((note)=>{return note._id!==id})
    const newNotes = [];
    notes.map((note)=>{
      if(note._id!==id)
      newNotes.push(note)

      return newNotes
    })
    setNotes(newNotes);
  }
  
  const editNote = (id, title, description, tag)=>{
    for (let index = 0; index < notes.length; index++) {
      const element = notes[index];
      if(element._id === id){
        element.title = title;
        element.description = description;
        element.tag = tag;
      }
    }
    // notes.map((note)=>{
    //   if(note._id===id)
    //     note.title = title
    //     note.description = description
    //     note.tag = tag
    // })
  }

  return (
    <NoteContext.Provider value={{ notes, addNote, deleteNote, editNote, getNotes }}>
      {props.children}
    </NoteContext.Provider>
  )
}

export default NoteState;
